END
GO

USE [HRDB]
GO

IF OBJECT_ID('DBO.DEPARTMENTS_SEQ') IS NULL
BEGIN
    CREATE SEQUENCE  "DBO"."DEPARTMENTS_SEQ"  MINVALUE 1 MAXVALUE 9990 INCREMENT BY 10 START WITH 280;
END
