IF OBJECT_ID('DBO.LOCATIONS_SEQ') IS NULL
BEGIN
    CREATE SEQUENCE  "DBO"."LOCATIONS_SEQ"  MINVALUE 1 MAXVALUE 9900 INCREMENT BY 100 START WITH 3300;
