    );
END
GO

IF OBJECT_ID('DBO.EMP_DETAILS_VIEW') IS NOT NULL
BEGIN
    DROP VIEW [DBO].[EMP_DETAILS_VIEW]
END
GO

CREATE VIEW [DBO].[EMP_DETAILS_VIEW]
    (
        "EMPLOYEE_ID", 
        "JOB_ID", 
        "MANAGER_ID", 
        "DEPARTMENT_ID", 
        "LOCATION_ID", 
        "COUNTRY_ID", 
        "FIRST_NAME", 
        "LAST_NAME", 
        "SALARY", 
        "COMMISSION_PCT", 
        "DEPARTMENT_NAME", 
        "JOB_TITLE", 
        "CITY", 
        "STATE_PROVINCE", 
        "COUNTRY_NAME", 
        "REGION_NAME"
    ) AS
    SELECT
        e.employee_id,
        e.job_id,
        e.manager_id,
        e.department_id,
        d.location_id,
        l.country_id,
        e.first_name,
        e.last_name,
        e.salary,
        e.commission_pct,
        d.department_name,
        j.job_title,
        l.city,
        l.state_province,
        c.country_name,
        r.region_name
    FROM
        employees e,
        departments d,
        jobs j,
        locations l,
        countries c,
         regions r
    WHERE e.department_id = d.department_id
        AND d.location_id = l.location_id
        AND l.country_id = c.country_id
        AND c.region_id = r.region_id
        AND j.job_id = e.job_id
GO

